-- 여행사 테이블 생성
CREATE TABLE IF NOT EXISTS travel_agencies (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  name TEXT NOT NULL,
  region TEXT NOT NULL,
  specialty TEXT NOT NULL,
  description TEXT NOT NULL,
  services TEXT NOT NULL,
  contact_person TEXT,
  phone TEXT,
  email TEXT,
  website TEXT,
  address TEXT,
  price_range TEXT,
  group_size TEXT,
  languages TEXT,
  certifications TEXT,
  rating REAL DEFAULT 4.5,
  review_count INTEGER DEFAULT 0,
  image_url TEXT,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP
);

-- 인덱스 생성
CREATE INDEX IF NOT EXISTS idx_travel_agencies_region ON travel_agencies(region);
CREATE INDEX IF NOT EXISTS idx_travel_agencies_specialty ON travel_agencies(specialty);

